sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	// If the client is a chairman account then perform the following
	if(current.company.x_kpmgh_audit_tran_chairman_account == true) {

		//Identify then client stakeholders linked to the selected company
		var execs = [];
		var gr = new GlideRecord('x_kpmgh_audit_tran_client_stakeholders');
		gr.addQuery('company',current.company);
		gr.query();
		while (gr.next()) {
			var exec = {};
			exec.name = gr.name.toString();
			exec.title = gr.title.toString();
			execs.push(exec);
		}

		//For each stakedholder create a meeting in the engagement tasks
		for(i=0; i<execs.length; i++) {
			var gr1 = new GlideRecord('x_kpmgh_audit_tran_engagement_tasks');
			gr1.initialize(); 
			gr1.parent = current.sys_id;
			gr1.assigned_to = current.assigned_to;
			gr1.short_description = 'C-suite meeting with ' + execs[i].name + ', ' + execs[i].title + '.';
			gr1.insert();
		}

		gs.addInfoMessage('Note. ' + current.company.getDisplayValue() + ' is a Chairman Account, ' + execs.length + ' C-suite meeting tasks created.');
	}

})(current, previous);
