sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	var stage = g_form.getValue('workflow_stage');
	var sections = g_form.getSections();
	//Hide the Pricing section during the Planning phase
	if(stage == 0) {
		sections[2].style.display = 'none';
	}	
	if(stage != 5) {
		sections[3].style.display = 'none';
	}	
	if(stage == 0 || stage == 1) {
		g_form.hideRelatedList('x_kpmgh_audit_tran_pbc');
	}
	if(stage == 0 || stage == 1 || stage == 2) {
		g_form.hideRelatedList('x_kpmgh_audit_tran_issues');
	}
	if(stage == 2) {
		g_form.showFieldMsg('short_description','Click the above icon to search for relevant Thought Leadership','info');
	}

}
