sys_script_include.script:
var ReturnItems = Class.create();
ReturnItems.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getItems: function() {

		var parent = this.getParameter('sysparm_parent');
		var items = [];

		var gr = new GlideRecord('x_kpmgh_audit_tran_pbc');
		gr.addQuery('parent',parent);
		gr.addQuery('state',1);
		gr.query();
		while (gr.next()) {
			var pbc = {};
			pbc.type = 'pbc';
			pbc.number = gr.number.toString();
			pbc.short_description = gr.short_description.toString();
			pbc.state = gr.state.toString();
			items.push(pbc);
		}

		var gr1 = new GlideRecord('x_kpmgh_audit_tran_issues');
		gr1.addQuery('parent',parent);
		gr1.addQuery('state',1);
		gr1.query();
		while (gr1.next()) {
			var issue = {};
			issue.type = 'issue';
			issue.number = gr1.number.toString();
			issue.short_description = gr1.short_description.toString();
			issue.state = gr1.state.toString();
			items.push(issue);
		}

		return JSON.stringify(items);

	},

    type: 'ReturnItems'
});
