sys_ui_action.script:
function results(){

	var ga = new GlideAjax('ReturnItems');
	ga.addParam('sysparm_name','getItems');
	ga.addParam('sysparm_parent',g_form.getUniqueValue());
	ga.getXMLAnswer(parseResponse);

	function parseResponse(response) {
		var items = JSON.parse(response);		
		if (items.length == 0) {
			gsftSubmit(null,g_form.getFormElement(),'results');
		} else {
			var message = 'Please conclude the following open items before proceeding:\n\n';
			for (i=0; i< items.length; i++) {
				message += items[i].number + ': ' + items[i].short_description + '.\n';
			}
			alert(message);
		}	
	}
}

if (typeof window == 'undefined') {
	current.workflow_stage = '5';
	//Do some stuff...

	//Update and redirect to current
	gs.addInfoMessage('Note. Engagement cycle complete! Please ensure you fill out the closeout checklist.');
	current.update();
	action.setRedirectURL(current);
}
